import Link from 'next/link';
import { Card, CardBody, CardImg, CardTitle, Col, Row } from 'reactstrap';
import { gql } from 'apollo-boost';
import { useQuery } from '@apollo/react-hooks';
import { useRouter } from 'next/router';
//graphQLを呼び出す
const GET_RESTAURANT_DISHES = gql`
  query ($id: ID!) {
    restaurant(id: $id) {
      id
      name
      dishes {
        id
        name
        description
        price
        image {
          url
        }
      }
    }
  }
`;

const Restaulants = ({ search }) => {
  // console.log('Restaurants.props:', props);

  const router = useRouter();
  //GraphQLを使うためのフックス。ここでAPIを叩く
  const { loading, error, data } = useQuery(GET_RESTAURANT_DISHES, {
    //下のqueryはRestaurantList/index.js内にある?id=${res.id}を指す
    variables: { search },
  });
  console.log('router.query.id:', router.query.id);
  console.log('data:', data);

  if (error) return 'レストランの読み込みに失敗しました。';

  if (loading) return <h1>読み込み中。。。</h1>;

  // console.log('props:', props.query);
  if (data) {
    const { restaurant } = data;
    return (
      <Row>
        {restaurant.dishes.map((res) => {
          return (
            <Col xs="6" sm="4" key={res.id}>
              <Card style={{ margin: '0 0.5rem 20px 0.5rem' }}>
                <CardImg
                  src={`${process.env.NEXT_PUBLIC_API_URL}${res.image.url}`}
                  top={true}
                  style={{ height: 250 }}
                />
                <CardBody>
                  <CardTitle>{res.name} </CardTitle>
                  <CardTitle>{res.description}</CardTitle>
                </CardBody>
                <div className="card-footer">
                  {/* 実際に飛ばされるのはas */}
                  <Link
                    as={`/restaurants/${res.id}`}
                    href={`/restaurants?${res.id}`}
                  >
                    <a className="btn btn-primary">もっと見る</a>
                  </Link>
                </div>
              </Card>
            </Col>
          );
        })}

        <style jsx>
          {`
            a {
              color: white;
            }
            a: link {
              text-decoration: none;
              color: white;
            }
            .card-column: 3;
          `}
        </style>
      </Row>
    );
  } else {
    return <h1>レストランが見つかりませんでした</h1>;
  }
};

export default Restaulants;
