import Link from 'next/link';
import { Card, CardBody, CardImg, CardTitle, Col, Row } from 'reactstrap';
import { gql } from 'apollo-boost';
import { useQuery } from '@apollo/react-hooks';

//graphQLを呼び出す
const query = gql`
  {
    restaurants {
      id
      name
      descriptioin
      image {
        url
      }
    }
  }
`;

const RestaulantList = () => {
  //GraphQLを使うためのフックス。ここでAPIを叩く
  const { loading, error, data } = useQuery(query);
  // console.log('data:', data);

  // if (data.restaurants && data.restaurants.length) {
  return (
    <Row>
      <Col xs="6" sm="4">
        <Card style={{ margin: '0 0.5rem 20px 0.5rem' }}>
          <CardImg
            src="http://localhost:1337/uploads/thumbnail_restaurant1_f33bc44de4.jpg"
            top={true}
            style={{ height: 250 }}
          />
          <CardBody>
            <CardTitle>Italian restaulant</CardTitle>
            <CardTitle>イタリアンのレストランです</CardTitle>
          </CardBody>
          <div className="card-footer">
            {/* 実際に飛ばされるのはas */}
            <Link href="/restaurants?id=1" as="restaulants/1">
              <a className="btn btn-primary">もっと見る</a>
            </Link>
          </div>
        </Card>
      </Col>

      <style jsx>
        {`
          a {
            color: white;
          }
          a: link {
            text-decoration: none;
            color: white;
          }
          .card-column: 3;
        `}
      </style>
    </Row>
  );
  // } else {
  //   return <h1>レストランが見つかりませんでした</h1>;
  // }
};

export default RestaulantList;
